DeviceProperties.ListenerScriptPacketParser:
IF (INDEXOF(sensor1_topic, "shellymotion2-deviceid") = 0)
    ADDERROR("Set correct 'sensor1_topic' value in module variables. Topic format is 'shellymotion2-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> Device info.");
    RETURN(-1);
END

VAR json := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(json, "Topic");
VAR topicPrefix := "shellies/" + sensor1_topic;

IF (INDEXOF(topic, topicPrefix + "/info") = 0)
    infoJson := PARSEJSON(json, "Payload");
    BOOL status := PARSEJSON(infoJson, "$.sensor.motion", 1);

    IF ISNULL(status)
        Rc := NaN;
    ELSE
        Rc := status;
    END

    VAR battery := PARSEJSON(infoJson, "$.bat.value");
    IF battery < 20
        ADDWARNING("Battery low (" + battery + "%)");
    END
END

