DeviceProperties.ListenerScriptPacketParser:
ADDINFO("To enable power monitoring, open device settings in Shelly app or web browser and configure device model in Settings->Device Model");

IF(INDEXOF(bulb1topic, "ShellyBulbDuo-deviceid") = 0)
    ADDERROR("Set correct 'bulb1topic' value in module variables. Topic format is 'ShellyBulbDuo-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> Device info.");
    RETURN(-1);
END

VAR data := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(data, "Topic");

IF INDEXOF(topic, "shellies/" + bulb1topic + "/light/0/power") = 0
    Ed := TODOUBLE(PARSEJSON(data, "Payload")) / 1000.0;
ELSEIF INDEXOF(topic, "shellies/" + bulb1topic + "/light/0/energy") = 0
    To := TODOUBLE(PARSEJSON(data, "Payload")) / 60000.0;
END

IF(INDEXOF(bulb1topic, "ShellyBulbDuo-deviceid") = 0)
    ADDERROR("Set correct 'bulb1topic' value in module variables. Topic format is 'ShellyBulbDuo-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> Device info.");
    RETURN(-1);
END

VAR data := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(data, "Topic");

IF INDEXOF(topic, "shellies/" + bulb1topic + "/light/0/status") != 0
    RETURN(0);
END

VAR json := PARSEJSON(data, "Payload", 1);

IF LENGTH(json) = 0
    RETURN(0);
END

VAR ison := PARSEJSON(json, "$.ison", 1);
VAR bright := PARSEJSON(json, "$.brightness");

IF ISNULL(ison) OR ISNULL(bright)
    RETURN(0);
END

Ct := PARSEJSON(json, "$.temp", 1);

IF ison
    Hb := bright / 100.0;
    St := isOn;
ELSE 
    Hb := 0;
    St := 0;
END

DeviceProperties.WriteBrightnessScriptPacketParser:
IF St > 0.5
    VAR topic := "shellies/" + bulb1topic + "/light/0/set";    
    MQTTPUBLISH(topic, "{\"turn\": \"on\", \"transition\": 300, \"brightness\": " + ROUND(Hb*100.0) + "}");
ELSE
    MQTTPUBLISH("shellies/" + bulb1topic + "/light/0/command", "off");
END

DeviceProperties.WriteCCTScriptPacketParser:
IF ISNAN(Ct)
    RETURN(0);
END

VAR topic := "shellies/" + bulb1topic + "/light/0/set";
MQTTPUBLISH(topic, "{\"transition\": 300, \"temp\":" + Ct + "}");

