DeviceProperties.ListenerScriptPacketParser:
ADDINFO("To enable power monitoring, open device settings in Shelly app or web browser and configure device model in Settings->Device Model");

IF(INDEXOF(bulb1topic, "shellycolorbulb-deviceid") = 0)
    ADDERROR("Set correct 'bulb1topic' value in module variables. Topic format is 'shellycolorbulb-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> Device info.");
    RETURN(-1);
END

VAR data := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(data, "Topic");

IF INDEXOF(topic, "shellies/" + bulb1topic + "/light/0/power") = 0
    Ed := TODOUBLE(PARSEJSON(data, "Payload")) / 1000.0;
ELSEIF INDEXOF(topic, "shellies/" + bulb1topic + "/light/0/energy") = 0
    To := TODOUBLE(PARSEJSON(data, "Payload")) / 60000.0;
END

IF(INDEXOF(bulb1topic, "shellycolorbulb-deviceid") = 0)
    ADDERROR("Set correct 'bulb1topic' value in module variables. Topic format is 'shellycolorbulb-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> Device info.");
    RETURN(-1);
END

VAR data := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(data, "Topic");

IF INDEXOF(topic, "shellies/" + bulb1topic + "/color/0/status") != 0
    RETURN(0);
END

VAR json := PARSEJSON(data, "Payload", 1);

IF LENGTH(json) = 0
    RETURN(0);
END

BOOL ison := PARSEJSON(json, "$.ison", 1);
VAR mode := PARSEJSON(json, "$.mode", 1);

IF ISNULL(mode)
    RETURN(0);
END

jsonStatus := json;
INT bright;

IF mode = "color"
    UINT8 red := PARSEJSON(jsonStatus, "$.red");
    UINT8 green := PARSEJSON(jsonStatus, "$.green");
    UINT8 blue := PARSEJSON(jsonStatus, "$.blue");
    bright := PARSEJSON(jsonStatus, "$.gain");

    VAR hsvColor := RGBTOHSV(red, green, blue);

    Hd := hsvColor.Hue;
    Sa := hsvColor.Saturation;
    Ct := NaN;
ELSE
    Ct := PARSEJSON(jsonStatus, "$.temp");
    bright := PARSEJSON(jsonStatus, "$.brightness");
END

IF ison
    Hb:= bright / 100.0;
    St := isOn;
ELSE
    Hb := 0;
    St := 0;
END

DeviceProperties.WriteHueScriptPacketParser:
IF !ISNAN(Ct)
    RETURN(0);
END

VAR topic := "shellies/" + bulb1topic + "/color/0/set";
VAR rgbColor := HSVTORGB(Hd, Sa, 1);

VAR data := "{\"mode\": \"color\", \"gain\": " + ROUND(Hb*100.0) + ", \"transition\":300,\"red\":" + rgbColor.red + ",\"green\":" + rgbColor.green + ",\"blue\":" + rgbColor.blue + "}";

MQTTPUBLISH(topic, data);

DeviceProperties.WriteBrightnessScriptPacketParser:
IF St > 0.5
    VAR topic := "shellies/" + bulb1topic + "/color/0/set";

	IF ISNAN(Ct)
        MQTTPUBLISH(topic, "{\"turn\": \"on\", \"transition\": 300, \"gain\": " + ROUND(Hb*100.0) + "}");
    ELSE
        MQTTPUBLISH(topic, "{\"turn\": \"on\", \"transition\": 300, \"brightness\": " + ROUND(Hb*100.0) + "}");

    END
ELSE
    MQTTPUBLISH("shellies/" + bulb1topic + "/color/0/command", "off");
END

DeviceProperties.WriteCCTScriptPacketParser:
IF ISNAN(Ct)
    RETURN(0);
END

VAR topic := "shellies/" + bulb1topic + "/color/0/set";

MQTTPUBLISH(topic, "{\"mode\": \"white\", \"brightness\": " + ROUND(Hb*100.0) + ", \"transition\": 300, \"temp\":" + Ct + "}");

