 var ret := "";
 var i := 0;
 do
 ret := ret + "(" + PARSEJSON(response.content, "$.devices[" + tostring(i) + "].deviceId", true) + ") ";
 ret := ret + PARSEJSON(response.content, "$.devices[" + tostring(i) + "].name", true);
 ret := ret + " - " + PARSEJSON(response.content, "$.devices[" + tostring(i) + "].type", true);
 ret := ret + "\n";
 i := i + 1;
 loop while i < max
 
 RETURN(ret);
END"}]

